import React from 'react';
import { 
  Paper,
  Typography 
} from '@material-ui/core';
import { useRecoilValue } from 'recoil';
import * as atoms from '../../navigation/atoms';

const styles = {
  container: {
    display: 'flex',
    alignItems: 'center',
    justifyContent: 'flex-start',
    width: '100%',
  } as React.CSSProperties,

  bank: {
    width: '7vh',
    height: '7vh'
  } as React.CSSProperties,

  descriptionContainer: {
    display: 'flex',
    alignItems: 'flex-start',
    justifyContent: 'flex-start',
    flexDirection: 'column',
    marginLeft: '1vh'
  } as React.CSSProperties
}

const Header = () => {
  const {account} = useRecoilValue(atoms.navigationAtom);

  return (
    <div style={styles.container}>
      <img style={styles.bank} 
            src={process.env.PUBLIC_URL + '/media/bank/america.svg'} />

      <div style={styles.descriptionContainer}>
        <Typography 
          variant='h2' 
          component='div'>
            {new Intl.NumberFormat('en-US').format(account.bankMoney)}
        </Typography>
      </div>
    </div>
  );
}

export default Header;
