import React from 'react';
import { 
  Paper,
  Typography 
} from '@material-ui/core';
import { useRecoilValue } from 'recoil';
import * as atoms from '../../navigation/atoms';

import CreditCard from '../../components/CreditCard/index';

const styles = {
  wrapper: {
    position: 'absolute', 
    left: '50%', 
    top: '50%',
    transform: 'translate(-50%, -50%)'
  } as React.CSSProperties,

  container: {
    display: 'flex',
    alignItems: 'flex-start',
    justifyContent: 'center',
    width: '80vw',
    height: '80vh',
    padding: '2vh'
  } as React.CSSProperties,

  top: {
    display: 'flex',
    alignItems: 'center',
    justifyContent: 'space-between',
    width: '100%',
    paddingLeft: '1vh'
  } as React.CSSProperties,

  leftContainer: {
    display: 'flex',
    alignItems: 'center',
    justifyContent: 'flex-start',
    width: '100%',
  } as React.CSSProperties,

  bank: {
    width: '7vh',
    height: '7vh'
  } as React.CSSProperties
}

const HomeScreen = () => {
  const {creditCards} = useRecoilValue(atoms.navigationAtom);

  return (
    <div style={styles.wrapper}>
      <Paper style={styles.container}>
        <div style={styles.top}>
          <div style={styles.leftContainer}>
            <img style={styles.bank} 
                 src={process.env.PUBLIC_URL + '/media/bank/america.svg'} />
          </div>
        </div>
      </Paper>
    </div>
  );
}

export default HomeScreen;
