import React, {
  useState, 
  useEffect
} from 'react';

import { makeStyles } from '@material-ui/core/styles';
import Table from '@material-ui/core/Table';
import TableBody from '@material-ui/core/TableBody';
import TableCell from '@material-ui/core/TableCell';
import TableContainer from '@material-ui/core/TableContainer';
import TableHead from '@material-ui/core/TableHead';
import TablePagination from '@material-ui/core/TablePagination';
import TableRow from '@material-ui/core/TableRow';

import { useRecoilState } from 'recoil';
import * as atoms from '../../navigation/atoms';
import CustomDateRangePicker from '../../components/CustomDateRangePicker';
import { BankService } from '../../services/service';
import { Balance } from '../../models/Balance';
import Loader from '../../components/Loader';

const styles = {
  container: {
    width: '100%',
    height: '40vh',
    display: 'flex',
    alignItems: 'flex-start',
    justifyContent: 'flex-start',
    flexDirection: 'column'
  } as React.CSSProperties,

  pickerContainer: {
    width: '50%',
    display: 'flex',
    alignItems: 'center',
    justifyContent: 'flex-end',
    alignSelf: 'flex-end',
    padding: '1vh'
  } as React.CSSProperties,

  table: {
    marginTop: '5vh'
  } as React.CSSProperties
};

const useStyles = makeStyles({
  root: {
    width: '100%',
  },
  container: {
    maxHeight: 440,
  },
});

const columns: any[] = [
  { 
    id: 'amount', 
    label: 'Quantia', 
    minWidth: 170,
    format: (value: number) => new Intl.NumberFormat('en-US', { 
      style: 'currency', 
      currency: 'USD'
    }).format(value)
  },

  { 
    id: 'createdAt', 
    label: 'Quando', 
    minWidth: 100,
    format: (value: number) => new Intl.NumberFormat('en-US', { 
      style: 'currency', 
      currency: 'USD'
    }).format(value)
  },

  {
    id: 'createdAt', 
    label: 'Quando', 
    minWidth: 100,
    format: (value: number) => {
      const date = new Date(value);

      return new Intl.DateTimeFormat('pt-BR', {
        weekday: 'short',
				year: 'numeric',
				month: 'long',
				day: 'numeric',
				hour: 'numeric',
				minute: 'numeric',
				second: 'numeric',
			}).format(date)
        .replaceAll('.','')
    }
  },
  {
    id: 'type', 
    label: 'Tipo', 
    minWidth: 100,
    format: (value: number) => {
      switch(value) {
        case 1: 
          return 'DepÃ³sito';
        case 2: 
          return 'Saque';
        case 3: 
          return 'Envio';
        case 4: 
          return 'Recebido';
      }
    }
  }
];


const BillsScreen = () => {
  const [page, setPage] = useState(0);
  const [{account}, setAccount] = useRecoilState(atoms.navigationAtom);
  const [data, setData] = useState({
    itemList: null,
    isAsyncRequesting: true
  });
  const rowsPerPage = 10;

  const receiptService = () => {
    async function receiptSubscriber() {
      const itemList = await BankService.getBalance(account.id, 1);

      setData({
        itemList: itemList,
        isAsyncRequesting: false 
      });
    }

    receiptSubscriber();
  }

  useEffect(receiptService, []);

  const classes = useStyles();

  return (
    <div style={styles.container}>
      <div style={styles.pickerContainer}>
        <CustomDateRangePicker />
      </div>  

      <div style={styles.table}>
        {data.isAsyncRequesting === true && (
          <Loader stroke={1}
            size={15}
            color={'rgb(255, 178, 33)'} /> 
        )}

        {data.isAsyncRequesting === false && (
          <>
            <TableContainer className={classes.container}>
              <Table stickyHeader aria-label="sticky table">
                <TableHead>
                  <TableRow>
                    {columns.map((column) => (
                      <TableCell
                        key={column.id}
                        align={column.align}
                        style={{ minWidth: column.minWidth }}
                      >
                        {column.label}
                      </TableCell>
                    ))}
                  </TableRow>
                </TableHead>
                <TableBody>
                  {(data.itemList ?? []).slice(page * rowsPerPage, page * rowsPerPage + rowsPerPage).map((row) => {
                    return (
                      <TableRow hover role="checkbox" tabIndex={-1} key={row.code}>
                        {columns.map((column) => {
                          const value = row[column.id];
                          return (
                            <TableCell key={column.id} align={column.align}>
                              {column.format && typeof value === 'number' ? column.format(value) : value}
                            </TableCell>
                          );
                        })}
                      </TableRow>
                    );
                  })}
                </TableBody>
              </Table>
            </TableContainer>
            <TablePagination
              rowsPerPageOptions={[10, 25, 100]}
              component="div"
              count={rows.length}
              rowsPerPage={rowsPerPage}
              page={page}
              onChangePage={handleChangePage}
              onChangeRowsPerPage={handleChangeRowsPerPage}
            />
          </>
        )}

      </div>
    </div>
  );
}

export default BillsScreen;
