import * as React from 'react';
import * as PropTypes from 'prop-types';
import { DateIOFormats } from '@date-io/core/IUtils';
import { MuiPickersAdapter } from './_shared/hooks/useUtils';

export const MuiPickersAdapterContext = React.createContext<MuiPickersAdapter | null>(null);

export interface LocalizationProviderProps {
  dateAdapter: new (...args: any) => MuiPickersAdapter;
  children: React.ReactNode;
  locale?: any;
  dateLibInstance?: any;
  dateFormats?: Partial<DateIOFormats>;
}

export const LocalizationProvider: React.FC<LocalizationProviderProps> = ({
  dateAdapter: Utils,
  children,
  locale,
  dateFormats: libFormats,
  dateLibInstance: libInstance,
}) => {
  const utils = React.useMemo(
    () => new Utils({ locale, formats: libFormats, instance: libInstance }),
    [Utils, locale, libFormats, libInstance]
  );

  return <MuiPickersAdapterContext.Provider value={utils} children={children} />;
};

LocalizationProvider.propTypes = {
  dateAdapter: PropTypes.func.isRequired,
  locale: PropTypes.oneOfType([PropTypes.object, PropTypes.string]),
  /**
   * Your component tree.
   */
  children: PropTypes.node.isRequired,
} as any;

export default LocalizationProvider;
import { useContext, useRef } from 'react';
import { IUtils } from '@date-io/core/IUtils';
import { MaterialUiPickersDate } from '../../typings/date';
import { MuiPickersAdapterContext } from '../../LocalizationProvider';

export type MuiPickersAdapter = IUtils<MaterialUiPickersDate>;

// TODO uncomment when syntax will be allowed by next babel
function checkUtils(utils: MuiPickersAdapter | null) /* : asserts utils is MuiPickersUtils */ {
  if (!utils) {
    throw new Error(
      'Can not find utils in context. It looks like you forgot to wrap your component in LocalizationProvider, or pass dateAdapter prop directly.'
    );
  }
}

export function useUtils() {
  const utils = useContext(MuiPickersAdapterContext);
  checkUtils(utils);

  return utils!;
}

export function useNow() {
  const utils = useUtils();
  const now = useRef(utils.date());

  return now.current;
}
export const DIALOG_WIDTH = 320;

export const DIALOG_WIDTH_WIDER = 325;

export const VIEW_HEIGHT = 358;

export const DAY_SIZE = 36;

export const DAY_MARGIN = 2;

export const IS_TOUCH_DEVICE_MEDIA = '@media (pointer: fine)';
