import React, {useEffect} from 'react';
import { 
  Button,
  Typography,
  withStyles
} from '@material-ui/core';
import { useRecoilState } from 'recoil';
import * as atoms from '../../bottomNavigation/atoms';

const styles = {
  container: {
    display: 'flex',
    alignItems: 'flex-start',
    justifyContent: 'flex-start',
    flexDirection: 'column'
  } as React.CSSProperties,

  icon: {
    width: '3vh',
    height: '3vh'
  } as React.CSSProperties,

  endAligner: {
    marginTop: '5vh'
  } as React.CSSProperties
}

const TheButton = withStyles({
  root: {
    minWidth: '12vh',
    maxWidth: '12vh',
    width: '12vh  !important',
    height: '12vh !important',
    padding: '0 !important',
    borderRadius: '0 !important',
    border: 'none !important',
    clipPath: 'polygon(5px 0, 100% 0, 100% 100%, 0 100%, 0 5px)',
    backgroundColor: 'rgb(34, 34, 37)',
    color: 'white'
  },

  label: {
    '&:hover': {
      color: 'black !important'
    }
  }
})(Button);

const ActionButton = (props: any) => {
  const [, setNavigation] = useRecoilState(atoms.bottomNavigationAtom);
  const handleTabIndex = () => {
    setNavigation(prevState => ({ 
      ...prevState,
      index: props.index
    })); 
  };

  return (
    <TheButton onClick={handleTabIndex}
            variant='contained'
            color='primary'>
      <div style={styles.container}>
        <img style={styles.icon} 
             src={process.env.PUBLIC_URL + '/media/ui/' + props.icon} /> 

        <Typography 
          variant='body2' 
          component='div'
          style={styles.endAligner}>
          {props.name}
        </Typography>
      </div>  
    </TheButton>
  );
}

export default ActionButton;
