import React, {useEffect, useState} from 'react';
import { 
  Button,
  Typography,
  withStyles
} from '@material-ui/core';
import { useRecoilState } from 'recoil';
import * as atoms from '../../bottomNavigation/atoms';

const styles = {
  container: {
    position: 'relative',
    width: '100%',
    display: 'flex',
    alignItems: 'flex-start',
    justifyContent: 'flex-start',
    flexDirection: 'column',
    boxSizing: 'border-box',
    padding: '1vh'
  } as React.CSSProperties,

  icon: {
    width: '3vh',
    height: '3vh'
  } as React.CSSProperties,

  endAligner: {
    position: 'absolute',
    bottom: 0,
    left: 0
  } as React.CSSProperties,

  dark: {
    filter: 'brightness(0)'
  } as React.CSSProperties
}

const TheButton = withStyles({
  root: {
    minWidth: '12vh',
    maxWidth: '12vh',
    width: '12vh  !important',
    height: '12vh !important',
    padding: '0 !important',
    borderRadius: '0 !important',
    border: 'none !important',
    clipPath: 'polygon(5px 0, 100% 0, 100% 100%, 0 100%, 0 5px)',
    backgroundColor: 'rgb(24, 24, 27)'
  },

  label: {
    '&:hover': {
      color: 'black !important'
    }
  }
})(Button);

const HoveredButton = withStyles({
  root: {
    minWidth: '12vh',
    maxWidth: '12vh',
    width: '12vh  !important',
    height: '12vh !important',
    padding: '0 !important',
    borderRadius: '0 !important',
    border: 'none !important',
    clipPath: 'polygon(5px 0, 100% 0, 100% 100%, 0 100%, 0 5px)',
    backgroundColor: '#18fc0a'
  }
})(Button);

const ActionButton = (props: any) => {
  const [navigation, setNavigation] = useRecoilState(atoms.bottomNavigationAtom);
  const [isHover, setHover] = useState<boolean>(false);
  const handleTabIndex = () => {
    setNavigation(prevState => ({ 
      ...prevState,
      index: props.index
    })); 
  };

  const handleOnMouseEnter = () => {
    setHover(true);
  };

  const handleOnMouseLeave = () => {
    setHover(false);
  };

  return (
    <>
      {navigation.index === props.index && (
        <HoveredButton 
            onClick={handleTabIndex}
            onMouseEnter={handleOnMouseEnter}
            onMouseLeave={handleOnMouseLeave}
            variant='contained'
            color='primary'>
          <div style={styles.container}>
            <img style={{...styles.icon, ...styles.dark}} 
                src={process.env.PUBLIC_URL + '/media/ui/' + props.icon} /> 

            <Typography 
              variant='body2' 
              component='div'
              style={{...styles.endAligner, ...styles.dark}}>
              {props.name}
            </Typography>
          </div>  
        </HoveredButton>
      )}

      {navigation.index !== props.index && (
        <TheButton 
            onClick={handleTabIndex}
            onMouseEnter={handleOnMouseEnter}
            onMouseLeave={handleOnMouseLeave}
            variant='contained'
            color='primary'>
          <div style={styles.container}>
            <img style={isHover ? {...styles.icon, ...styles.dark} : styles.icon} 
                src={process.env.PUBLIC_URL + '/media/ui/' + props.icon} /> 

            <Typography 
              variant='body2' 
              component='div'
              style={isHover ? {...styles.endAligner, ...styles.dark} : styles.endAligner}>
              {props.name}
            </Typography>
          </div>  
        </TheButton>
      )}
    </>
  );
}

export default ActionButton;
