import React, {
  useState, 
  useEffect
} from 'react';
import { 
  Typography,
  TextField,
  withStyles
} from '@material-ui/core';
import NumberFormat from 'react-number-format';
import axios from 'axios';

import { useRecoilState } from 'recoil';
import * as atoms from '../../navigation/atoms';
import CustomDateRangePicker from '../../components/CustomDateRangePicker';
import { BankService } from '../../services/service';

const styles = {
  container: {
    width: '100%',
    height: '40vh',
    display: 'flex',
    alignItems: 'flex-start',
    justifyContent: 'flex-start',
    flexDirection: 'column'
  } as React.CSSProperties,

  pickerContainer: {
    width: '50%',
    display: 'flex',
    alignItems: 'center',
    justifyContent: 'flex-end',
    alignSelf: 'flex-end',
    padding: '1vh'
  } as React.CSSProperties,

  icon: {
    width: '3vh',
    height: '3vh'
  } as React.CSSProperties,

  withDescription: {
    display: 'flex',
    alignItems: 'flex-start',
    justifyContent: 'flex-start',
    flexDirection: 'column'
  } as React.CSSProperties,

  wrapper: {
    marginTop: '5vh',
    width: '30vw'
  } as React.CSSProperties
}

const BillsScreen = () => {
  const [data, setAccount] = useRecoilState(atoms.navigationAtom);

  const receiptService = () => {
    async function receiptSubscriber() {
      await BankService.getBalance(1, 1);
    }

    receiptSubscriber();
  }

  useEffect(receiptService, []);

  return (
    <div style={styles.container}>
      <div style={styles.pickerContainer}>
        <CustomDateRangePicker />
      </div>  
    </div>
  );
}

export default BillsScreen;
