import React from 'react';
import { 
  Paper,
  Typography 
} from '@material-ui/core';
import { useRecoilValue } from 'recoil';
import * as atoms from '../../navigation/atoms';

const styles = {
  container: {
    display: 'flex',
    alignItems: 'center',
    justifyContent: 'flex-start',
    width: '100%',
    marginTop: '-3vh'
  } as React.CSSProperties,

  bank: {
    width: '10vh',
    height: '10vh'
  } as React.CSSProperties,

  descriptionContainer: {
    display: 'flex',
    alignItems: 'flex-start',
    justifyContent: 'flex-start',
    flexDirection: 'column',
    marginLeft: '1vh'
  } as React.CSSProperties,

  row: {
    display: 'flex',
    alignItems: 'center',
    justifyContent: 'center',
  },

  icon: {
    width: '2vh',
    height: '2vh'
  } as React.CSSProperties,

  amount: {
    marginLeft: '1vh'
  } as React.CSSProperties,

  money: {
    marginLeft: '1vh',
    color: '#9f9f91 !important'
  } as React.CSSProperties
}

const Header = () => {
  const {account} = useRecoilValue(atoms.navigationAtom);

  return (
    <div style={styles.container}>
      <img style={styles.bank} 
            src={process.env.PUBLIC_URL + '/media/bank/america.svg'} />

      <div style={styles.descriptionContainer}>
        <Typography 
          variant='body2' 
          component='div'>
            {account.name}
        </Typography>

        <div style={styles.row}>
          <img style={styles.icon} 
               src={process.env.PUBLIC_URL + '/media/ui/wallet.svg'} />

          <Typography 
            variant='h2' 
            component='div'
            style={styles.money}>
              {new Intl.NumberFormat('en-US', { 
                style: 'currency', 
                currency: 'USD'
              }).format(account.money)}
          </Typography>
        </div>

        <div style={styles.row}>
          <img style={styles.icon} 
               src={process.env.PUBLIC_URL + '/media/ui/creditcard.svg'} />

          <Typography 
            variant='h2' 
            component='div'
            style={styles.amount}>
              {new Intl.NumberFormat('en-US', { 
                style: 'currency', 
                currency: 'USD'
              }).format(account.bankMoney)}
          </Typography>
        </div>
      </div>
    </div>
  );
}

export default Header;
