import { createMuiTheme } from '@material-ui/core/styles';
import Tomorrow from './tomorrow';

export default createMuiTheme({
  palette: {
    primary: {
      light: '#757ce8',
      main: '#18fc0a',
      dark: '#002884',
      contrastText: '#fff'
    },
    secondary: {
      light: '#ff7961',
      main: '#f44336',
      dark: '#ba000d',
      contrastText: '#000',
    },
  },

  typography: {
    fontFamily: Tomorrow.fontFamily,
  },

  overrides: {
    MuiCssBaseline: {
      '@global': {
        '@font-face': [Tomorrow],
      },
    },

    MuiTypography: {
      h1: {
        fontSize: 16,
        fontWeight: 400,
        color: '#9f9f91',
        alignSelf: 'flex-start'
      },

      h2: {
        fontSize: 16,
        fontWeight: 400,
        color: '#dfdfe1',
        alignSelf: 'flex-start'
      },

      subtitle1: {
        fontSize: 22,
        fontWeight: 300,
        color: '#dfdfe1'
      },

      subtitle2: {
        fontSize: 22,
        fontWeight: 'bold',
        color: '#dfdfe1'
      },

      body1: {
        fontSize: 16,
        fontWeight: 'bold',
        color: '#bfbfb1',
      },

      body2: {
        fontSize: 10,
        fontWeight: 500,
        color: '#bfbfb1',
      },

      h4: {
        fontSize: 9,
        fontWeight: 'bold',
        color: '#bfbfb1',
      },

      caption: {
        fontSize: 12,
        fontWeight: 400,
        color: '#dfdfe1 !important'
      }
    },

    MuiCircularProgress: {
      colorPrimary: {
        color: '#bfbfb1'
      }
    },

    MuiPaper: {
      root: {
        clipPath: 'polygon(0 0, calc(100% - 5px) 0px, 100% 5px, 100% 100%, 5px 100%, 0 calc(100% - 5px), 0 50%, 4px calc(50% - 4px), 4px calc(15% + 4px), 0 15%)',
        background: `radial-gradient(circle, transparent 20%, rgb(4, 4, 7) 20%, rgb(4, 4, 7) 80%, transparent 80%, transparent), 
                     radial-gradient(circle, transparent 20%, rgb(4, 4, 7) 20%, rgb(4, 4, 7) 80%, transparent 80%, transparent) 25px 25px, 
                     linear-gradient(rgb(24, 24, 27) 2px, transparent 2px) 0 -1px, 
                     linear-gradient(90deg, rgb(24, 24, 27) 2px, transparent 2px) -1px 0;`,
        backgroundColor: 'rgb(4, 4, 7)',
        backgroundSize: '150px 150px, 150px 150px, 25px 25px, 25px 25px',
        padding: '2vh !important',
      }
    },

    MuiFormHelperText: {
      root: {
        fontSize: 9,
        fontWeight: 'bold',
        color: '#bfbfb1',
      }
    },

    MuiFormControlLabel: {
      root: {
        marginLeft: '0 !important'
      },

      label: {
        fontSize: '12px !important',
        fontWeight: 400
      }
    },

    MuiFormLabel: {
      root: {
        color: '#18fc0a',
        textAlign: 'center'
      }
    },

    MuiPopover: {
      paper: {
        padding: '0 !important'
      }
    },

    MuiButtonBase: {
      root: {
        background: 'transparent'
      }
    },

    MuiIconButton: {
      root: {
        color: '#bfbfb1 !important'
      },

      label: {
        fontSize: 14,
        color: '#bfbfb1 !important'
      }
    },

    MuiToolbar: {
      root: {
        backgroundColor: 'transparent !important',
        padding: '0 !important',
        paddingLeft: '2vh !important',
        paddingRight: '2vh !important',
        paddingTop: '1vh !important',
        height: 'initial !important',
        minHeight: '0 !important'
      }
    },

    MuiTabs: {
      indicator: {
        height: 1,
        background: '#18fc0a'
      },
    },

    MuiTab: {
      root: {
        minWidth: '0 !important',
        height: 30,
        minHeight: 30,
        textTransform: 'none',
        fontWeight: 400,
        fontSize: 10,
        color: '#dfdfe1',
        textAlign: 'start',
        '&$selected': {
          color: '#1890ff',
          fontWeight: 'bold',
        },
      },

      textColorPrimary: {
        color: '#dfdfe1',
      }
    },

    MuiStepper: {
      root: {
        background: 'transparent !important',
        border: 'none !important',
        boxShadow: 'none !important',
        padding: '1vh !important',
        paddingBottom: '0 !important',
        boxSizing: 'border-box',
        margin: 0
      }
    },

    MuiMenuItem: {
      root: {
        fontSize: 16,
        fontWeight: 300,
        color: '#dfdfe1',
        '&:hover': {
          backgroundColor: 'rgb(54, 54, 57)'
        }
      }
    },

    MuiStepLabel: {
      label: {
        color: '#bfbfb1 !important'
      }
    },

    MuiSwitch: {
      switchBase: {
        color: 'rgb(44, 44, 47)',
        '& + $track': {
          backgroundColor: 'rgb(44, 44, 47)',
        },

        '&.Mui-checked': {
          color: '#18fc0a !important',
          '& + $track': {
            backgroundColor: '#18fc0a !important',
          },
        }
      }
    },

    MuiTextField: {
      root: {
        width: '100%',
        minHeight: 0
      }
    },

    MuiFilledInput: {
      root: {
        transition: 'all .3s'
      },

      underline: {
        '&&&:before': {
          borderBottom: 'none'
        },
        '&&:after': {
          borderBottom: 'none'
        }
      }
    },

    MuiInput: {
      input: {
        '&:-webkit-autofill': {
          WebkitBoxShadow: '0 0 0 1000px rgb(14, 14, 17) inset',
          WebkitTextFillColor: '#bfbfb1',
        }
      }
    },

    MuiSnackbarContent: {
      root: {
        paddingTop: '0 !important',
        paddingBottom: '0 !important',
        paddingLeft: '1vh',
        paddingRight: '1vh',
        fontSize: '10px',
        fontWeight: 400,
        border: '1px solid transparent',
        backgroundColor: 'rgb(225, 0, 80)',
        borderRadius: 0
      }
    },

    MuiInputLabel: {
      root: {
        color: '#dfdfe1',
        fontSize: 12,
        lineHeight: '23px',
        zIndex: 100,
        marginLeft: '1vh',
        pointerEvents: 'none',

        '&$focused': { 
          marginLeft: 0,
          lineHeight: '23px !important'
        },

        '&$error': { 
          marginLeft: 0,
        },

        '&.Mui-disabled': {
          color: '#dfdfe1',
          fontSize: 12,
          lineHeight: '0',
          opacity: 0.7
        },
      },
    },

    MuiSelect: {
      icon: {
        color: '#bfbfb1',
        marginRight: '1.5vh'
      }
    },

    MuiInputBase: {
      inputAdornedEnd: {
        paddingLeft: '1vh'
      },

      input: {
        '&::placeholder': {
          color: '#dfdfe1 !important',
          opacity: '1 !important',
        },
        paddingLeft: '1vh !important',
        paddingRight: '1vh !important',

        '&::selection' : {
          backgroundColor: 'rgba(#750afc, .7)'
        },
      },

      root: {
        minHeight: 0,
        background: `none`,
        border: 'none',
        fontSize: 25,
        fontWeight: 300,
        color: '#dfdfe1',
        textTransform: 'none',
        transition: 'all .3s',
        paddingBottom: 3,

        '&:hover' : {
          border: 'none',
          background: `none`,
        },

        '&.Mui-focused': {
          border: 'none',
          background: `none`,
        },

        '&.Mui-disabled': {
          background: `none`,
          border: '1px solid rgba(255, 255, 255, .1)',
          opacity: .7,
        },

        '&.Mui-error': {
          color: '#ff003c',
          border: '1px solid rgba(252, 10, 24, .1)',
          background: `none`
        },
      },
    },

    MuiButton: {
      text: {
        textTransform: 'none',
        fontSize: 14,
        fontWeight: 'bold',
        alignSelf: 'center',

        ['@media (max-width: 767px)']: { 
          alignSelf: 'center !important',
        }
      },

      outlined: {
        textTransform: 'none',
        fontSize: 14,
        paddingTop: '1px',
        paddingBottom: '1px',
        paddingLeft: '6px',
        paddingRight: '6px',
        borderRadius: 'none !important',
        clipPath: 'polygon(0 0, calc(100% - 10px) 0, 100% 10px, 100% 100%, 10px 100%, 0 calc(100% - 10px))',

        ['@media (max-width: 767px)']: { 
          fontSize: 14
        },

        '&.Mui-disabled': {
          color: 'inherit'
        },
      },

      contained: {
        backgroundColor: '#18fc0a',
        border: '2px solid #18fc0a',
        borderRadius: 'none !important',
        fontWeight: 500,
        fontSize: 14,
        textTransform: 'none',
        color: 'black !important',
        boxShadow: 'none !important',
        padding: '0',
        transition: 'all .3s',
        margin: 0,
        clipPath: 'polygon(10px 0, 100% 0, 100% calc(100% - 10px), calc(100% - 10px) 100%, 0 100%, 0 10px)', 

        '&:hover' : {
          backgroundColor: '#18fc0a !important',
          boxShadow: '0 0 5px #18fc0a, 0 0 5px #18fc0a !important'
        },

        '&.Mui-disabled': {
          backgroundColor: 'rgb(14, 14, 17) !important',
          border: '2px solid rgb(14, 14, 17)',
          color: '#666 !important'
        },

        ['@media (max-width: 767px)']: {
          fontSize: 14
        }
      }
    },
  },
});
