import React from 'react';
import { 
  Paper,
  Button,
  Typography,
  withStyles
} from '@material-ui/core';
import { useRecoilValue } from 'recoil';
import * as atoms from '../../navigation/atoms';

const styles = {
  container: {
    display: 'flex',
    alignItems: 'center',
    justifyContent: 'space-between',
    width: '100%',
    marginTop: '-3vh'
  } as React.CSSProperties,

  bank: {
    width: '10vh',
    height: '10vh'
  } as React.CSSProperties,

  moneyContainer: {
    display: 'flex',
    alignItems: 'center',
    justifyContent: 'center',
    flexDirection: 'column'
  } as React.CSSProperties,

  row: {
    display: 'flex',
    alignItems: 'center',
    justifyContent: 'center',
  },

  icon: {
    width: '2vh',
    height: '2vh'
  } as React.CSSProperties,

  amount: {
    marginLeft: '1vh'
  } as React.CSSProperties,

  money: {
    marginLeft: '1vh',
    color: '#9f9f91 !important'
  } as React.CSSProperties,

  chevron: {
    marginTop: '-1vh'
  } as React.CSSProperties,

  button: {
    width: '3vh  !important',
    height: '3vh !important',
    padding: '0 !important'
  } as React.CSSProperties
}

const LogoutButton = withStyles({
  root: {
    minWidth: '4vh',
    maxWidth: '4vh',
    width: '4vh  !important',
    height: '4vh !important',
    padding: '0 !important'
  }
})(Button);

const Header = () => {
  const {account} = useRecoilValue(atoms.navigationAtom);

  const handleClose = () => {
    fetch(`http://drip_login/OnBarberCancel`, {
      method: 'POST',
      body: JSON.stringify({})
    });

    document.onkeydown = null;
    fetch(`http://drip_bank/OnBankClose`, {
      method: 'POST',
      body: JSON.stringify({})
    });
  };

  return (
    <div style={styles.container}>
      <img style={styles.bank} 
            src={process.env.PUBLIC_URL + '/media/bank/america.svg'} />

      <div style={styles.moneyContainer}>
        <Typography 
          variant='body2' 
          component='div'>
            {account.name}
        </Typography>

        <Typography 
          variant='caption' 
          component='div'>
            Dinheiro da carteira {new Intl.NumberFormat('en-US', { 
              style: 'currency', 
              currency: 'USD'
            }).format(account.money)}
        </Typography>

        <Typography 
          variant='caption' 
          component='div'
          style={styles.chevron}>âŒ„</Typography>
      </div>

      <LogoutButton onClick={handleClose}
                  variant='outlined'
                  color='primary'>
          a
      </LogoutButton>
    </div>
  );
}

export default Header;
