import React, {useEffect} from 'react';
import { 
  Paper,
  Button,
  Typography,
  withStyles
} from '@material-ui/core';
import { useRecoilState } from 'recoil';
import * as atoms from '../../navigation/atoms';

const styles = {
  container: {
    display: 'flex',
    alignItems: 'center',
    justifyContent: 'space-between',
    width: '100%',
    marginTop: '-3vh'
  } as React.CSSProperties,

  bank: {
    width: '10vh',
    height: '10vh'
  } as React.CSSProperties,

  moneyContainer: {
    display: 'flex',
    alignItems: 'center',
    justifyContent: 'center',
    flexDirection: 'column'
  } as React.CSSProperties,

  row: {
    display: 'flex',
    alignItems: 'center',
    justifyContent: 'center',
  },

  icon: {
    width: '2vh',
    height: '2vh'
  } as React.CSSProperties,

  amount: {
    marginLeft: '1vh'
  } as React.CSSProperties,

  money: {
    marginLeft: '1vh',
    color: '#9f9f91 !important'
  } as React.CSSProperties,

  chevron: {
    marginTop: '-2vh',
    fontSize: '3vh'
  } as React.CSSProperties,

  button: {
    width: '3vh  !important',
    height: '3vh !important',
    padding: '0 !important'
  } as React.CSSProperties
}

const LogoutButton = withStyles({
  root: {
    minWidth: '4vh',
    maxWidth: '4vh',
    width: '4vh  !important',
    height: '4vh !important',
    padding: '0 !important',
    borderRadius: '0 !important',
    border: 'none !important',
    clipPath: 'polygon(5px 0, 100% 0, 100% 100%, 0 100%, 0 5px)',
    backgroundColor: 'rgb(34, 34, 37)',
    color: 'white'
  },

  label: {
    color: '#dfdfe1',
    '&:hover': {
      color: 'black !important'
    }
  }
})(Button);

const Header = () => {
  const [{account}, setNavigation] = useRecoilState(atoms.navigationAtom);

  const handleClose = () => {
    document.onkeydown = null;
    setNavigation(prevState => ({ 
      ...prevState,
      index: -1
    })); 

    fetch(`http://drip_bank/OnBankClose`, {
      method: 'POST',
      body: JSON.stringify({})
    });
  };

  const keyDown = (event: any) => {
    if(event.keyCode === 27 || event.keyCode === 69) {
      handleClose();
    }
  }

  const handleKeyPress = () => {
    document.onkeydown = keyDown;
  }

  useEffect(handleKeyPress, []);

  return (
    <div style={styles.container}>
      <img style={styles.bank} 
            src={process.env.PUBLIC_URL + '/media/bank/america.svg'} />

      <div style={styles.moneyContainer}>
        <Typography 
          variant='body2' 
          component='div'>
            {account.name}
        </Typography>

        <Typography 
          variant='caption' 
          component='div'>
            Dinheiro da carteira {new Intl.NumberFormat('en-US', { 
              style: 'currency', 
              currency: 'USD'
            }).format(account.money)}
        </Typography>

        <Typography 
          variant='caption' 
          component='div'
          style={styles.chevron}>⌄</Typography>
      </div>

      <LogoutButton onClick={handleClose}
                  variant='contained'
                  color='primary'>
          ➝
      </LogoutButton>
    </div>
  );
}

export default Header;
