import React, { useEffect } from 'react';
import { useRecoilState } from 'recoil';
import HomeScreen from '../screens/Home';

import * as atoms from './atoms';

const Navigation = () => {
  const [navigation, setNavigation] = useRecoilState(atoms.navigationAtom);

  const handleNavigation = () => {
    const onData = (data: any) => {
      switch(true) {
        case data.show === true: 
          setNavigation(prevState => ({ 
            ...prevState,
            index: 0,
            account: data.account
          }));
          break;
        case data.hide === true: 
          setNavigation(prevState => ({ 
            ...prevState,
            index: -1
          }));
          document.onkeydown = null;
          break;
        case data.asyncRequesting === true: 
          setNavigation(prevState => ({ 
            ...prevState,
            isAsyncRequesting: data.asyncState
          }));
          break;
        case data.transfer === true: 
          setNavigation(prevState => ({ 
            ...prevState,
            account: {
              ...prevState.account, 
              ...{
                bankMoney: navigation.account.bankMoney + data.amount
              }
            },
            isAsyncRequesting: false
          }));
          break;
      }
    }

    window.addEventListener('message', (event) => onData(event.data));
  }

  useEffect(handleNavigation, []);

  switch(navigation.index) {
    case 0: 
      return (<HomeScreen />);
    default: 
      return (<></>);
  }
}

export default Navigation;
