import React, {useState} from 'react';
import { 
  Typography,
  TextField,
  withStyles
} from '@material-ui/core';
import NumberFormat from 'react-number-format';
import { useRecoilState } from 'recoil';
import * as atoms from '../../navigation/atoms';
import SubmitButtonWithSelector from '../../components/SubmitButtonWithSelector';

const styles = {
  container: {
    width: '100%',
    height: '40vh',
    display: 'flex',
    alignItems: 'center',
    justifyContent: 'center',
    flexDirection: 'column'
  } as React.CSSProperties,

  buttonContainer: {
    width: '100%',
    display: 'flex',
    alignItems: 'center',
    justifyContent: 'center',
    flexDirection: 'column',
    boxSizing: 'border-box',
    padding: '1vh'
  } as React.CSSProperties,

  icon: {
    width: '3vh',
    height: '3vh'
  } as React.CSSProperties,

  withDescription: {
    display: 'flex',
    alignItems: 'flex-start',
    justifyContent: 'flex-start',
    flexDirection: 'column'
  } as React.CSSProperties,

  wrapper: {
    marginTop: '1vh',
    width: '30vw'
  } as React.CSSProperties
}

interface NumberFormatCustomProps {
  inputRef: (instance: NumberFormat | null) => void;
  onChange: (event: { target: { name: string; value: string } }) => void;
  name: string;
}

function NumberFormatCustom(props: NumberFormatCustomProps) {
  const {inputRef, onChange, ...other} = props;

  return (
    <NumberFormat
      {...other}
      getInputRef={inputRef}
      onValueChange={(values) => {
        onChange({
          target: {
            name: props.name,
            value: values.value,
          },
        });
      }}
      thousandSeparator
      isNumericString
      prefix="$"
    />
  );
}

const BillsScreen = () => {
  const [data, setAccount] = useRecoilState(atoms.navigationAtom);
  const isDisabled = data.account.bankMoney < data.account.bills;

  const handleDeposit = () => {
    fetch(`http://drip_bank/PayBills`, {
      method: 'POST',
      body: JSON.stringify({
        amount: data.account.bills
      })
    });
    
    setAccount(prevState => ({ 
      ...prevState,
      account: {
        ...prevState.account, 
        ...{
          bills: 0,
          bankMoney: data.account.bankMoney - data.account.bills
        }
      },
      isAsyncRequesting: true
    }));
  }

  return (
    <div style={styles.container}>
      <div style={styles.buttonContainer}>
        <div style={styles.withDescription}>
          <Typography 
            variant='subtitle2' 
            component='div'>
            Deseja mesmo pagar suas multas?
          </Typography>

          <div style={styles.withDescription}>
            <Typography 
              variant='body2' 
              component='div'>
              Todas as multas pendentes somam
            </Typography>

            <Typography 
              variant='subtitle1' 
              component='div'>
              {new Intl.NumberFormat('en-US', { 
                style: 'currency', 
                currency: 'USD'
              }).format(data.account.bills)}
            </Typography>
          </div>
        </div>
      </div>  

      <div style={styles.wrapper}>
        <SubmitButtonWithSelector
            label='Pagar âž'
            isDisabled={isDisabled}
            isAsyncRequesting={data.isAsyncRequesting}
            onClick={handleDeposit}
            fullWidth={true} />
      </div>
    </div>
  );
}

export default BillsScreen;
