import React, {useState} from 'react';
import { 
  Typography,
  TextField,
  withStyles
} from '@material-ui/core';
import NumberFormat from 'react-number-format';

import { useRecoilState } from 'recoil';
import * as atoms from '../../navigation/atoms';
import SubmitButtonWithSelector from '../../components/SubmitButtonWithSelector';
import CustomDateRangePicker from '../../components/CustomDateRangePicker';

const styles = {
  container: {
    width: '100%',
    height: '40vh',
    display: 'flex',
    alignItems: 'flex-start',
    justifyContent: 'flex-start',
    flexDirection: 'column'
  } as React.CSSProperties,

  pickerContainer: {
    width: '70%',
    display: 'flex',
    alignItems: 'center',
    justifyContent: 'flex-end',
    alignSelf: 'flex-end',
    padding: '1vh'
  } as React.CSSProperties,

  icon: {
    width: '3vh',
    height: '3vh'
  } as React.CSSProperties,

  withDescription: {
    display: 'flex',
    alignItems: 'flex-start',
    justifyContent: 'flex-start',
    flexDirection: 'column'
  } as React.CSSProperties,

  wrapper: {
    marginTop: '1vh',
    width: '30vw'
  } as React.CSSProperties
}

const BillsScreen = () => {
  const [data, setAccount] = useRecoilState(atoms.navigationAtom);
  const isDisabled = data.account.bankMoney < data.account.bills;

  const handleDeposit = () => {
    fetch(`http://drip_bank/PayBills`, {
      method: 'POST',
      body: JSON.stringify({
        amount: data.account.bills
      })
    });
    
    setAccount(prevState => ({ 
      ...prevState,
      account: {
        ...prevState.account, 
        ...{
          bills: 0,
          bankMoney: data.account.bankMoney - data.account.bills
        }
      },
      isAsyncRequesting: true
    }));
  }

  return (
    <div style={styles.container}>
      <div style={styles.pickerContainer}>
        <CustomDateRangePicker />
      </div>  

      <div style={styles.wrapper}>
        <SubmitButtonWithSelector
            label='Pagar ➝'
            isDisabled={isDisabled}
            isAsyncRequesting={data.isAsyncRequesting}
            onClick={handleDeposit}
            fullWidth={true} />
      </div>
    </div>
  );
}

export default BillsScreen;
