import React from 'react';
import {Typography} from '@material-ui/core';
import {useRecoilValue} from 'recoil';
import * as atoms from '../../navigation/atoms';

const styles = {
  container: {
    width: '100%',
    display: 'flex',
    alignItems: 'center',
    justifyContent: 'space-around'
  },

  buttonContainer: {
    display: 'flex',
    alignItems: 'flex-start',
    justifyContent: 'space-between',
    flexDirection: 'column',
    boxSizing: 'border-box',
    padding: '1vh',
    minWidth: '15vw',
    maxWidth: '15vh',
    width: '15vw',
    height: '40vh',
    borderRadius: '0',
    border: 'none !important',
    clipPath: 'polygon(5px 0, 100% 0, 100% 100%, 0 100%, 0 5px)',
    backgroundColor: 'rgb(14, 14, 17)'
  } as React.CSSProperties,

  icon: {
    width: '3vh',
    height: '3vh'
  } as React.CSSProperties,

  withDescription: {
    display: 'flex',
    alignItems: 'flex-start',
    justifyContent: 'flex-start',
    flexDirection: 'column'
  } as React.CSSProperties,

  withSpace: {
    marginLeft: '1vh'
  } as React.CSSProperties
}

const DetailsScreen = () => {
  const {account} = useRecoilValue(atoms.navigationAtom);

  return (
    <div style={styles.container}>
        <div style={styles.buttonContainer}>
          <img style={styles.icon} 
              src={process.env.PUBLIC_URL + '/media/ui/balance.svg'} /> 

          <div style={styles.withDescription}>
            <Typography 
              variant='body2' 
              component='div'>
              Seu saldo atual
            </Typography>

            <Typography 
              variant='subtitle1' 
              component='div'>
              {new Intl.NumberFormat('en-US', { 
                style: 'currency', 
                currency: 'USD'
              }).format(account.bankMoney)}
            </Typography>
          </div>

          <Typography 
              variant='body2' 
              component='div'>
          Seu dinheiro com seguran√ßa
          </Typography>
        </div>  


        <div style={styles.buttonContainer}>
          <img style={styles.icon} 
              src={process.env.PUBLIC_URL + '/media/ui/bill.svg'} /> 

          <div style={styles.withDescription}>
            <Typography 
              variant='body2' 
              component='div'>
              Multas pendentes
            </Typography>

            <Typography 
              variant='subtitle1' 
              component='div'>
              {new Intl.NumberFormat('en-US', { 
                style: 'currency', 
                currency: 'USD'
              }).format(account.bills)}
            </Typography>
          </div>

          <Typography 
              variant='body2' 
              component='div'>
          Use-nos para pagar suas multas
          </Typography>
        </div>  
    </div>
  );
}

export default DetailsScreen;
