import {atom, selector} from 'recoil';

export const navigationAtom = atom({
  key: 'navigationAtom',
  default: {
    index: 0,
    account: {
      id: 1,
      name: 'Robson Cezario',
      bankMoney: 7000,
      money: 500,
      bills: 2000
    },
    withdrawInput: '0',
    creditCards: [
    {
      number: 7242,
      securityCode: 252,
      expirationDate: +(new Date())
    }],
    isAsyncRequesting: false
  } 
});

export const withdrawSelector = selector({
  key: 'withdrawSelector',
  get: ({get} : any) => {
    const data = get(navigationAtom);
    const withdraw = parseInt(data.withdrawInput);

    return {
      isDisabled: data.account.bankMoney > withdraw || withdraw < 1,
      isAsyncRequesting: data.isAsyncRequesting
    }
  },
});
