import React, { useEffect } from 'react';
import { useRecoilState, useRecoilValue } from 'recoil';
import ActionButton from '../components/ActionButton';

import * as atoms from './atoms';

const styles = {
  container: {
    display: 'flex',
    alignItems: 'center',
    justifyContent: 'center',
  } as React.CSSProperties,

  wrapper: {
    marginLeft: '1vh'
  } as React.CSSProperties
}

const BottomNavigation = () => {
  const {index} = useRecoilValue(atoms.bottomNavigationAtom);
  const buttonList = [
  {
    name: 'Transferencia',
    icon: 'transfer.svg'
  },

  {
    name: 'Depositar',
    icon: 'deposit.svg'
  },

  {
    name: 'Depositar',
    icon: 'withdraw.svg'
  },

  {
    name: 'Pagar multas',
    icon: 'withdraw.svg'
  }]

  return (
    <div style={styles.container}>
      {buttonList.map((b, index) => (
        <>
          {index === 0 &&
            <ActionButton
              name={b.name}
              icon={b.icon}
              index={index} />
          }

          {index > 0 &&
            <div style={styles.wrapper}>
              <ActionButton
                name={b.name}
                icon={b.icon}
                index={index} />
            </div>
          }
        </>
      ))}
    </div>
  )
}

export default BottomNavigation;
