import React from 'react';
import { 
  Paper,
  Typography 
} from '@material-ui/core';
import { useRecoilValue } from 'recoil';
import * as atoms from '../../navigation/atoms';

import CreditCard from '../../components/CreditCard/index';

const styles = {
  wrapper: {
    position: 'absolute', 
    left: '50%', 
    top: '50%',
    transform: 'translate(-50%, -50%)'
  } as React.CSSProperties,

  container: {
    display: 'flex',
    alignItems: 'center',
    justifyContent: 'center',
    width: '80vw',
    height: '80vh'
  } as React.CSSProperties,

  column: {
    display: 'flex',
    alignItems: 'center',
    justifyContent: 'flex-start',
    flexDirection: 'column',
    width: '15vw',
    height: '80vh',
    backgroundColor: 'blue'
  } as React.CSSProperties,

  spacer: {
    marginTop: '2vh'
  } as React.CSSProperties,

  content: {
    display: 'flex',
    alignItems: 'center',
    justifyContent: 'flex-start',
    flexDirection: 'column',
    width: '50vw',
    height: '80vh',
    backgroundColor: 'red'
  } as React.CSSProperties,

  buttonList: {
    width: '15vw',
    height: '80vh',
    display: 'flex',
    alignItems: 'center',
    justifyContent: 'flex-start',
    flexDirection: 'column',
    backgroundColor: 'yellow'
  } as React.CSSProperties
}

const HomeScreen = () => {
  const {creditCards} = useRecoilValue(atoms.navigationAtom);

  return (
    <div style={styles.wrapper}>
      <Paper style={styles.container}>
        <div style={styles.column}>
          <Typography 
              variant='h2' 
              component='div'>
                Minha carteira
          </Typography>

          <div style={styles.spacer}></div>

          {creditCards.map(c => {
            return (<CreditCard data={c} />)
          })}
        </div>

        <div style={styles.content}>
          <Typography 
              variant='h2' 
              component='div'>
                Balan√ßo atual
          </Typography>
        </div>

        <div style={styles.buttonList}>
          <Typography 
              variant='h2' 
              component='div'>
                Botoes
          </Typography>
        </div>
      </Paper>
    </div>
  );
}

export default HomeScreen;
