import React, {useEffect, useState} from 'react';
import { 
  Button,
  Typography,
  withStyles
} from '@material-ui/core';
import { useRecoilState } from 'recoil';
import * as atoms from '../navigation/atoms';

const styles = {
  container: {
    width: '100%',
    display: 'flex',
    alignItems: 'center',
    justifyContent: 'center'
  },

  buttonContainer: {
    width: '100%',
    display: 'flex',
    alignItems: 'flex-start',
    justifyContent: 'flex-start',
    flexDirection: 'column',
    boxSizing: 'border-box',
    padding: '1vh'
  } as React.CSSProperties,

  icon: {
    width: '3vh',
    height: '3vh'
  } as React.CSSProperties,

  endAligner: {
    marginTop: '5vh'
  } as React.CSSProperties,

  dark: {
    filter: 'brightness(0)'
  } as React.CSSProperties
}

const TheButton = withStyles({
  root: {
    minWidth: '15vw',
    maxWidth: '15vh',
    width: '15vw  !important',
    height: '30vh !important',
    padding: '0 !important',
    borderRadius: '0 !important',
    border: 'none !important',
    clipPath: 'polygon(5px 0, 100% 0, 100% 100%, 0 100%, 0 5px)',
    backgroundColor: 'rgb(24, 24, 27)'
  }
})(Button);

const DetailsScreen = () => {
  const [{account}, setNavigation] = useRecoilState(atoms.navigationAtom);

  return (
    <div style={styles.container}>
      <TheButton 
          variant='contained'
          color='primary'>
        <div style={styles.buttonContainer}>
          <img style={styles.icon} 
              src={process.env.PUBLIC_URL + '/media/ui/withdraw.png'} /> 

          <Typography 
            variant='body2' 
            component='div'
            style={styles.endAligner}>
            fala feio auhuaha
          </Typography>
        </div>  
      </TheButton>
    </div>
  );
}

export default DetailsScreen;
